import javax.swing.*;import java.awt.*;import java.awt.event.*;public class A0Test{    public static int current = 1;    public static JLabel label = new JLabel ();        public static void buttonAction ()    {	label.setText (Integer.toString (current));	current ++;    }        public static void main (String[] args)    {	JButton button = new JButton ("Click Me!");	button.addActionListener (new ActionListener () {		public void actionPerformed (ActionEvent e) { buttonAction(); }	    });	label.setText ("--------------------");	JPanel panel = new JPanel ();	panel.add (Box.createRigidArea (new Dimension (20,10)));	panel.add (button);	panel.add (Box.createRigidArea (new Dimension (20,10)));	panel.add (label);	panel.add (Box.createRigidArea (new Dimension (20,10)));	JFrame frame = new JFrame ("Program");	frame.getContentPane().add (panel, BorderLayout.WEST);	frame.addWindowListener(new WindowAdapter() {		public void windowClosing (WindowEvent e) { System.exit(0); }	    });	frame.pack ();	frame.setVisible(true);    }}